<template>
  <div id="app">
    <h1>vue-drag-verify-img-chip</h1>
    <h3>基于vue-drag-verify二次开发的vue组件</h3>
    <drag-verify-img-chip
      ref="dragVerify"
      :imgsrc="img"
      :isPassing.sync="isPassing"
      :showRefresh="true"
      :barWidth="40"
      handlerIcon="fa fa-angle-double-right"
      successIcon="fa fa-check-circle-o"
      refreshIcon="fa fa-refresh"
      @refresh="handleRefresh"
      @passcallback="handlePass"
      @passfail="handleFail"
    >
    </drag-verify-img-chip>
    <button
      style="margin-top:10px"
      @click="handleReset"
    >reset</button>

    <h2><a href="https://yimijianfang.github.io/vue-drag-verify/#/dragimgchip">doc&demo</a></h2>
  </div>
</template>

<script>
import dragVerifyImgChip from "./packages/vue-drag-verify-img-chip";
import t2 from "./assets/t2.png";
import t3 from "./assets/t3.png";
export default {
  name: "app",
  components: {
    dragVerifyImgChip
  },
  data() {
    return {
      isPassing: false,
      t2,
      t3,
      img: t2
    };
  },
  methods: {
    handleReset() {
      this.isPassing = false;
      this.$refs.dragVerify.reset();
    },
    handleRefresh() {
      this.img = this.img == this.t3 ? this.t2 : this.t3;
    },
    handlePass() {
      alert("success");
    },
    handleFail() {
      setTimeout(()=>{
        this.$refs.dragVerify.reset();
      },500)
    }
  }
};
</script>

